package com.abc.algorithms.leetcode.string;

import java.util.ArrayList;
import java.util.List;

public class _1573_SplitString {
    private static int numWays(String s) {
        List<Integer> onesIndices = new ArrayList<>();

        for (int idx = 0; idx < s.toCharArray().length; idx++)
            if (s.charAt(idx) == '1') onesIndices.add(idx);

        // If number of ones is not a multiple of 3, return 0
        if (onesIndices.size() % 3 != 0) return 0;

        /**
         *  If no ones
         *  There are n-1 borders possible for n zeros
         *  0 / 0 / 0 / 0
         *  Number of ways of placing n-1 borders in two positions is (n-1)C2 = (n-1)(n-2)/2
         */

        if (onesIndices.size() == 0) return (s.length() - 1) * (s.length() - 2) / 2;

        // Calculate number of zeros in between ranges
        int numWays = 1;
        int stepSize = onesIndices.size() / 3;
        while (stepSize < onesIndices.size()) {
            numWays *= onesIndices.get(stepSize) - onesIndices.get(stepSize - 1);
            stepSize += stepSize;
        }

        return numWays;
    }

    public static void main(String[] args) {
        System.out.println(numWays(""));
    }
}
